import { Observable } from 'rxjs';
import { AuthService } from './../auth/auth.service';
import { Component, ComponentFactoryResolver, OnInit, enableProdMode } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent implements OnInit {
  // Declare Properties
  form: FormGroup;
  submitted: boolean = false;
  username: string;
  element: any;

  constructor(private formBuilder: FormBuilder,
    private router: Router,
    public authService: AuthService) { 
      // Create Form Group
      this.form = this.formBuilder.group(
        {
          username: [null, [Validators.required]],
          password: [null, [Validators.required]],
          type: [null, [Validators.required]]
        }
      );
  }

  ngOnInit(): void {
    this.element = document.getElementById('divMessage');
  }

  // Method to Validate Form & Authenticate User
  async onSubmit() {
    this.submitted = true;
    if (this.form.invalid) {
      return;
    }
    // Call login Method to Check User Authentication
    await this.authService.login(this.form.get('username').value, this.form.get('password').value, this.form.get('type').value);
    if (this.authService.isLoggedIn === false) {
      this.element.style.display = 'block';
    } else {
      this.element.style.display = 'none';
    }
  }
}
<div class="row">
    <div class="col-md-4 offset-md-4">
        <div class="login-form bg-light mt-4 p-4">
            <form [formGroup]="form" (ngSubmit)="onSubmit()" class="row g-3">
                <div class="text-center">
                    <h3>Login Form</h3>
                    <div id="divMessage" style="display: none;" class="alert alert-danger" role="alert">
                      Please Check Your Credentials
                    </div>
                </div>
                <div class="form-group col-12">
                  <label>Username</label>
                  <input
                    type="text"
                    formControlName="username"
                    class="form-control"
                    [ngClass]="{ 'is-invalid': submitted && form.get('username').errors }"
                  />
                </div>

                <div class="form-group col-12">
                  <label>Password</label>
                  <input
                    type="password"
                    formControlName="password"
                    class="form-control"
                    [ngClass]="{ 'is-invalid': submitted && form.get('password').errors }"
                  />
                </div>

                <div class="form-group col-12">
                  <label>Type</label>
                  <select formControlName="type" class="form-control" [ngClass]="{ 'is-invalid': submitted && form.get('type').errors }">
                      <option value="user">User</option>
                      <option value="admin">Admin</option>
                  </select>
                </div>

                <div class="form-group">
                  <button type="submit" class="btn btn-success float-end">Login</button>
                </div>
              </form>
        </div>
    </div>
  </div>
